UPDATE "documentation"."arguments"SET "comment" = 'The PostgreSQL specific name of the function'WHERE "argument" = 'input_specific_name'AND "specific_name" ~* '^get_function_definition(_)+([0-9])+$';UPDATE "documentation"."functions"SET "example" = $$SELECT api.get_function_definition('create_dns_zone_147865');$$, "comment" = 'Generate the function definition for documentation.', "schema" = 'documentation'WHERE "specific_name" ~* '^get_function_definition(_)+([0-9])+$';