<div class="system_resource_title_container">	<span class="resource_title_container_span"><?echo "$system_name ($comment)";?></span>	<img class="resource_title_container_img"src="http://www.google.com/images/nav_logo72.png"></img></div><div class="system_resource_information_container">	<table>		<tr><td><em>Owner:</em></td><td><?echo $owner;?></td></tr>		<tr><td><em>Type:</em></td><td><?echo $type;?></td></tr>		<tr><td><em>OS:</em></td><td><?echo $os_name;?></td></tr>		<tr><td><em>Date Created:</em></td><td><?echo $date_created;?></td></tr>		<tr><td><em>Date Modified:</em></td><td><?echo $date_modified;?></td></tr>		<tr><td><em>Last Modifier:</em></td><td><?echo $last_modifier;?></td></tr>	</table></div>